import { useHistory } from "react-router";
import PokemonCard from "../../../../components/PokemonCard/PokemonCard";
import { useState, useEffect, useContext } from "react";
import s from "./style.module.css";
import database from "../../../../service/firebase";
import cn from "classnames";
import { FirebaseContext } from "../../../../components/context/firebaseContext";
import { PokemonContext } from "../../../../components/context/pokemonContext";

const StartPage = () => {
  const firebase = useContext(FirebaseContext);
  const pokemonsContext = useContext(PokemonContext);
  const [pokemons, setPokemons] = useState({});

  useEffect(() => {
    firebase.getPokemons((pokemons) => setPokemons(pokemons));
    return () => {
      firebase.offPockemonSocket();
    };
  }, []);

  const history = useHistory();
  const handlerClick = () => {
    history.push("/");
  };
  const handlerSelect = (key) => {
    const pokemon = { ...pokemons[key] };
    pokemonsContext.onSelectedPokemons(key, pokemon);
    setPokemons((prevState) => ({
      ...prevState,
      [key]: {
        ...prevState[key],
        selected: !prevState[key].selected,
      },
    }));
  };

  const handlerGoBoard = () => {
    history.push("/game/board");
  };

  return (
    <div>
      <div className={s.flex}>
        <button className={cn(s.button)} onClick={handlerGoBoard}>
          Start Game
        </button>
      </div>
      <div className={s.flex}>
        {Object.entries(pokemons).map(([key, pokemon]) => (
          <PokemonCard
            className={s.card}
            key={key}
            objId={key}
            values={pokemon.values}
            name={pokemon.name}
            img={pokemon.img}
            id={pokemon.id}
            type={pokemon.type}
            isActive={true}
            isSelected={pokemon.selected}
            handlerSelect={() => {
              if (
                Object.keys(pokemonsContext.pokemons).length < 5 ||
                pokemon.selected
              ) {
                handlerSelect(key);
              }
            }}
          />
        ))}
      </div>
      <button className="button" onClick={handlerClick}>
        Back To HomePage
      </button>
    </div>
  );
};

export default StartPage;
