import { useHistory } from "react-router";
import PokemonsJSON from "../../data/Pokemons.json";
import PokemonCard from "../../components/PokemonCard/PokemonCard";
import { useState, useEffect } from "react";
import s from "./style.module.css";
import database from "../../service/firebase";
import cn from "classnames";

const GamePage = () => {
  const POKEMONS = PokemonsJSON;
  const [pokemons, setPokemons] = useState(() => {
    [...POKEMONS];
  });

  useEffect(() => {
    database.ref("pokemons").once("value", (snapshot) => {
      setPokemons(snapshot.val());
      Object.entries(snapshot.val()).map((i) => {
        i.active = false;
      });
    });
  }, []);

  const history = useHistory();
  const handlerClick = () => {
    history.push("/");
  };
  const flipCard = (id, objId) => {
    setPokemons((prevState) => {
      return Object.entries(prevState).reduce((acc, item) => {
        const pokemon = { ...item[1] };
        if (item[0] === objId) {
          pokemon.active = !pokemon.active;
          database.ref("pokemons/" + objId).set({
            ...pokemon,
          });
        }

        acc[item[0]] = pokemon;
        console.log(acc);
        return acc;
      }, {});
    });
  };

  const addNewPokemon = () => {
    const newKey = database.ref().child("pokemons").push().key;
    database.ref("pokemons/" + newKey).set(POKEMONS[1]);
  };
  return (
    <div>
      <div className={s.flex}>
        <button className={cn(s.button)} onClick={addNewPokemon}>
          ADD NEW POKEMON
        </button>
      </div>
      <div className={s.flex}>
        {Object.entries(pokemons).map(([key, pokemon]) => (
          <PokemonCard
            key={key}
            objId={key}
            values={pokemon.values}
            name={pokemon.name}
            img={pokemon.img}
            id={pokemon.id}
            type={pokemon.type}
            isActive={pokemon.active}
            flipCard={flipCard}
          />
        ))}
      </div>
      <button className="button" onClick={handlerClick}>
        Back To HomePage
      </button>
    </div>
  );
};

export default GamePage;
