import { useHistory } from "react-router";
import PokemonCard from "../../../../components/PokemonCard/PokemonCard";
import { useState, useEffect, useContext } from "react";
import s from "./style.module.css";
import database from "../../../../service/firebase";
import cn from "classnames";
import { FirebaseContext } from "../../../../components/context/firebaseContext";

const GamePage = () => {
  const firebase = useContext(FirebaseContext);
  const [pokemons, setPokemons] = useState({});

  useEffect(() => {
    firebase.getPokemons((pokemons) => setPokemons(pokemons));
    return () => {
      firebase.offPockemonSocket();
    };
  }, []);

  const history = useHistory();
  const handlerClick = () => {
    history.push("/");
  };
  const handlerChangeActive = (key) => {
    setPokemons((prevState) => {
      ({
        ...prevState,
        [key]: {
          ...prevState[key],
          selected: !prevState[key].selected,
        },
      });
    });
  };

  return (
    <div>
      <div className={s.flex}>
        <button className={cn(s.button)}>Start Game</button>
      </div>
      <div className={s.flex}>
        {Object.entries(pokemons).map(([key, pokemon]) => (
          <PokemonCard
            className={s.card}
            key={key}
            objId={key}
            values={pokemon.values}
            name={pokemon.name}
            img={pokemon.img}
            id={pokemon.id}
            type={pokemon.type}
            isActive={true}
            isSelected={pokemon.selected}
            handlerChangeActive={() => handlerChangeActive(key)}
          />
        ))}
      </div>
      <button className="button" onClick={handlerClick}>
        Back To HomePage
      </button>
    </div>
  );
};

export default GamePage;
