import { useState } from "react";
import LoginForm from "../../components/LoginForm";
import Modal from "../../components/Modal/Modal";
import Menu from "../Menu/Menu";
import Navbar from "../Navbar/Navbar";
import { NotificationManager } from "react-notifications";

const MenuHeader = ({ bgActive }) => {
  const [isOpenModal, setOpenModal] = useState(false);
  const [active, setActive] = useState(false);
  const changeState = () => {
    setActive((prevState) => !prevState);
  };

  const handlerClickLogin = () => {
    setOpenModal((prevState) => !prevState);
  };

  const handleSubmitLoginForm = async ({ email, password }) => {
    const requestOptions = {
      method: "POST",
      body: JSON.stringify({
        email,
        password,
        returnSecureToken: true,
      }),
    };
    const responce = await fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyBYS5eAvMigqgwYdAL_bG_yu4FLz7_2MbQ",
      requestOptions
    ).then((res) => res.json());
    console.log(responce);
    NotificationManager.error("error", "title");
  };

  return (
    <div>
      <Menu checkActive={active} changeActive={changeState} />
      <Navbar
        checkActive={active}
        bgActive={bgActive}
        changeActive={changeState}
        onClickLogin={handlerClickLogin}
      />

      <Modal
        isOpen={isOpenModal}
        onCloseModal={handlerClickLogin}
        title="LOG IN"
      >
        <LoginForm onSubmit={handleSubmitLoginForm} />
      </Modal>
    </div>
  );
};

export default MenuHeader;
