const Input = ({ value, label, type = "text", name, onChange, required }) => {
  return (
    <div class={s.root}>
      <input
        name={name}
        type={type}
        value={value}
        class="input"
        onChange={onChange}
        required={required}
      />
      <span class={s.highlight}></span>
      <span class={s.bar}></span>
      <label class={s.label}>{label}</label>
    </div>
  );
};

export default Input;
