import { useContext } from "react";
import { PokemonContext } from "../../../../components/context/pokemonContext";
import s from "./style.module.css";
import PokemonCard from "../../../../components/PokemonCard/PokemonCard";
import { useHistory } from "react-router-dom";
import cn from "classnames";
import { useState } from "react";
const FinishPage = () => {
  //  const handlerSelect = (key) => {
  //    const pokemon = { ...pokemons[key] };
  //    pokemonsContext.onSelectedPokemons(key, pokemon);
  //    setPokemons((prevState) => ({
  //      ...prevState,
  //      [key]: {
  //        ...prevState[key],
  //        selected: !prevState[key].selected,
  //      },
  //    }));
  //  };

  // handlerSelect={() => handlerSelect(key)}

  //  const handlerClick = () => {
  //   handlerSelect && handlerSelect(key);
  // };
  // return (
  //   <div
  //     className={cn(className, s.pokemonCard, {
  //       [s.active]: isActive,
  //       [s.selected]: isSelected,
  //     })}
  //     onClick={handlerClick}
  //   ></div>

  const history = useHistory();

  const { pokemons, clearContext, pokemons2, winner } =
    useContext(PokemonContext);
  const [cardSelected, setCardSelected] = useState(null);

  const handleCardClick = (card) => {
    if (winner !== 1) {
      setCardSelected(null);
      return;
    }
    setCardSelected(card);
  };

  console.log("$$$$$$: ", pokemons2);
  return (
    <div>
      <div className={s.flex}>
        {Object.values(pokemons).map((card) => {
          return <PokemonCard {...card} isActive={true}></PokemonCard>;
        })}
      </div>
      <div className={cn(s.flex, s.button)}>
        <button
          onClick={() => {
            clearContext();
            history.push("/game");
          }}
        >
          PLAY AGAIN
        </button>
      </div>
      <div className={s.flex}>
        {pokemons2.map((card, index) => {
          return (
            <PokemonCard
              onClick={() => {
                console.log("click");
                // handleCardClick(card);
              }}
              key={index}
              id={card.id}
              name={card.name}
              type={card.type}
              img={card.img}
              values={card.values}
              possession="red"
              className={cn(s.card, s["card--red"], {
                [s.selected]: card.id === (cardSelected || {}).id,
              })}
              isActive
              minimize
            ></PokemonCard>
          );
        })}
      </div>
    </div>
  );
};

export default FinishPage;
